document.addEventListener("DOMContentLoaded",(()=>{setTimeout((()=>{document.querySelectorAll(".sc-gallery").forEach((e=>{const t=e.querySelectorAll("img");t[0]&&t[0].classList.add("active");let c=0;e.addEventListener("click",(()=>{t[c].classList.remove("active"),c=(c+1)%t.length,t[c].classList.add("active")}))}))}),0)}));